import {Card,Typography,} from "@material-tailwind/react";
import categoriesArr from "../utils/catergories";
import { useState } from "react";



function submitHandler(e){
  e.preventDefault();
}


function changeHandler(e){

}



function ExpenseForm(){

const [expense, setExpense] = useState({
  description: "",
  amount: 0.00,
  category: "",
})

return(
     <Card color="white" className="p-6 w-[80%] h-[80%]" shadow={false}>
      <form className="mt-8 mb-2" onSubmit={submitHandler}>
        <div className="mb-1 flex flex-col gap-6">
          <Typography variant="h2" color="blue-gray" className="mb-4">
            Add/Edit a Expense
          </Typography>
          <Typography variant="h5" color="blue-gray">
            Expense Description
          </Typography>
          <input
            required
            name="description"
            placeholder="Ex: Car Repair"
            onChange = {changeHandler}
            className="border-2 border-black p-2 rounded-md mb-2 font-bold text-black"
          />
          <Typography variant="h5" color="blue-gray">
            Amount
          </Typography>
           <input
            name="amount"
            type="number"
            size="sm"
            required
            min="0"
            step="0.01"
            placeholder="0.00"
            onChange = {changeHandler}
            className="border-2 border-black p-2 rounded-md mb-2 font-bold text-black"
          />
          <Typography variant="h5" color="blue-gray">
            Category
          </Typography>
          <select   onChange={changeHandler} name="category" className="p-3 border-2 rounded-md border-black mb-2 font-bold text-black">
           {categoriesArr.map( (c)=>{
              return <option key={c.id} value={c.id}>{c.value}</option>
            })}
          </select>
        </div>
        <button className="mt-6 font-[500] text-white p-3 rounded-md bg-green-700" >
          Complete
        </button>
      </form>
    </Card>
)

}

export default ExpenseForm;
